(function(){'use strict';function initDashboardTabs(){const tabs=document.querySelectorAll('[data-tab]');const tabContents=document.querySelectorAll('.tab-content');tabs.forEach(function(tab){tab.addEventListener('click',function(){const tabName=this.getAttribute('data-tab');tabs.forEach(function(t){t.classList.remove('active');t.setAttribute('aria-selected','false');});tabContents.forEach(function(content){content.classList.remove('active');});this.classList.add('active');this.setAttribute('aria-selected','true');const selectedContent=document.getElementById(tabName+'-tab');if(selectedContent){selectedContent.classList.add('active');}});});const refreshBtn=document.querySelector('[data-action="refresh-downloads"]');if(refreshBtn){refreshBtn.addEventListener('click',function(){location.reload();});}}if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',initDashboardTabs);}else{initDashboardTabs();}})();