(function(){'use strict';function initMaterialCards(){document.addEventListener('click',function(e){const card=e.target.closest('.material-card[data-material-url]');if(card&&!e.target.closest('a,button')){const url=card.getAttribute('data-material-url');if(url){window.location.href=url;}}});}function initCategoryCards(){document.addEventListener('click',function(e){const card=e.target.closest('.category-card[data-category-url]');if(card){const url=card.getAttribute('data-category-url');if(url){window.location.href=url;}}});}function initStopPropagation(){document.addEventListener('click',function(e){if(e.target.hasAttribute('data-stop-propagation')||e.target.closest('[data-stop-propagation]')){e.stopPropagation();}});}if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',function(){initMaterialCards();initCategoryCards();initStopPropagation();});}else{initMaterialCards();initCategoryCards();initStopPropagation();}})();