(function(){'use strict';const LoadingStates={showSpinner:function(message){const spinner=document.getElementById('loading-spinner');const text=document.getElementById('loading-text');if(spinner){if(text&&message){text.textContent=message;}spinner.style.display='block';spinner.setAttribute('aria-busy','true');}},hideSpinner:function(){const spinner=document.getElementById('loading-spinner');if(spinner){spinner.style.display='none';spinner.setAttribute('aria-busy','false');}},showOverlay:function(message){const overlay=document.getElementById('loading-overlay');const text=document.getElementById('loading-overlay-text');if(overlay){if(text&&message){text.textContent=message;}overlay.style.display='flex';overlay.setAttribute('aria-busy','true');document.body.style.overflow='hidden';}},hideOverlay:function(){const overlay=document.getElementById('loading-overlay');if(overlay){overlay.style.display='none';overlay.setAttribute('aria-busy','false');document.body.style.overflow='';}},showInline:function(elementId,message){const inline=document.getElementById('loading-inline');if(inline){const text=inline.querySelector('.loading-text-small');if(text&&message){text.textContent=message;}inline.style.display='inline-flex';}},hideInline:function(){const inline=document.getElementById('loading-inline');if(inline){inline.style.display='none';}},setButtonLoading:function(button,loading){if(!button)return;if(loading){button.classList.add('loading');button.disabled=true;button.setAttribute('aria-busy','true');if(!button.dataset.originalText){button.dataset.originalText=button.textContent;}}else{button.classList.remove('loading');button.disabled=false;button.removeAttribute('aria-busy');if(button.dataset.originalText){button.textContent=button.dataset.originalText;}}},setFormLoading:function(form,loading){if(!form)return;if(loading){form.classList.add('form-loading');const inputs=form.querySelectorAll('input,button,select,textarea');inputs.forEach(input=>{input.disabled=true;});}else{form.classList.remove('form-loading');const inputs=form.querySelectorAll('input,button,select,textarea');inputs.forEach(input=>{input.disabled=false;});}},setTableLoading:function(table,loading){if(!table)return;if(loading){table.classList.add('table-loading');}else{table.classList.remove('table-loading');}},setCardLoading:function(card,loading){if(!card)return;if(loading){card.classList.add('card-loading');}else{card.classList.remove('card-loading');}}};function enhanceForms(){const forms=document.querySelectorAll('form[data-loading]');forms.forEach(form=>{form.addEventListener('submit',function(e){const loadingType=form.dataset.loading||'form';if(loadingType==='overlay'){LoadingStates.showOverlay('Processing your request...');}else if(loadingType==='spinner'){LoadingStates.showSpinner('Processing your request...');}else{LoadingStates.setFormLoading(form,true);}});});}function enhanceButtons(){const buttons=document.querySelectorAll('button[data-loading],a[data-loading]');buttons.forEach(button=>{button.addEventListener('click',function(e){const loadingType=button.dataset.loading||'button';if(loadingType==='overlay'){LoadingStates.showOverlay('Processing...');}else if(loadingType==='spinner'){LoadingStates.showSpinner('Processing...');}else{LoadingStates.setButtonLoading(button,true);}});});}function enhanceAjaxRequests(){const originalFetch=window.fetch;window.fetch=function(...args){const url=args[0];const options=args[1]||{};if(!options.noLoading){LoadingStates.showSpinner('Loading...');}return originalFetch.apply(this,args).then(response=>{if(!options.noLoading){LoadingStates.hideSpinner();}return response;}).catch(error=>{if(!options.noLoading){LoadingStates.hideSpinner();}throw error;});};}function init(){enhanceForms();enhanceButtons();}window.LoadingStates=LoadingStates;if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',init);}else{init();}})();